function itemToggle(e,t,s){for(let r=0;r<e.length;r++)t[r].addEventListener("click",(()=>{for(let r=0;r<t.length;r++)e[r].classList.remove(s+"--current"),t[r].classList.remove("toggles__button--current");e[r].classList.add(s+"--current"),t[r].classList.add("toggles__button--current")}))}const reviews=document.querySelectorAll(".reviews__block"),reviewsSlider=document.querySelector(".reviews__toggles"),reviewsButtons=reviewsSlider.querySelectorAll(".toggles__button"),reviewsClassName="reviews__block";itemToggle(reviews,reviewsButtons,"reviews__block");